    <link rel="stylesheet" href="/style.css">
    <style>
        .dashboard-layout {
            display: flex;
            flex-direction: column;
            gap: 20px;
            width: 100%;
            max-width: 900px;
        }
        .header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            color: white;
            padding: 10px 0;
        }
        .section {
            background: white;
            padding: 20px;
            border-radius: 8px;
        }
        .section h3 { margin-top: 0; }
        .message-list { max-height: 300px; overflow-y: auto; }
        .send-form { display: flex; flex-direction: column; gap: 10px; }
        .send-form input, .send-form textarea {
            padding: 10px;
            border: 1px solid #ccc;
            border-radius: 4px;
        }
        .logout-btn {
            color: white;
            text-decoration: none;
            background: #ff4d4d;
            padding: 5px 15px;
            border-radius: 4px;
        }
    </style>
</head>
<body>
    <div class="dashboard-layout">
        <div class="header">
            <h1>BlueMind Messages</h1>
            <div>
                <span id="welcome-msg"></span>
                <a href="/logout" class="logout-btn">Logout</a>
            </div>
        </div>

        <div class="section">
            <h3>Send a Message</h3>
            <form class="send-form" action="/send-message" method="POST">
                <input type="text" name="receiver_username" placeholder="To: (Username)" required>
                <textarea name="message" placeholder="Type your message here..." rows="3" required></textarea>
                <button type="submit" class="btn-connect" style="width: 100%; margin-top: 10px;">Send</button>
            </form>
        </div>

        <div class="section">
            <h3>Your Messages</h3>
            <div id="message-container" class="message-list">
                <p>Loading messages...</p>
            </div>
        </div>
    </div>

    <script>
        // Fetch and display messages
        async function loadMessages() {
            try {
                const response = await fetch('/messages');
                const messages = await response.json();
                const container = document.getElementById('message-container');

                if (messages.length === 0) {
                    container.innerHTML = '<p>No messages yet.</p>';
                    return;
                }

                container.innerHTML = messages.map(msg => `
                    <div class="message">
                        <div class="message-sender">${msg.sender_name} <span class="message-time">${new Date(msg.timestamp).toLocaleString()}</span></div>
                        <div class="message-text">${msg.message}</div>
                    </div>
                `).join('');
            } catch (err) {
                console.error("Failed to load messages", err);
            }
        }

        loadMessages();
    </script>
</body>
</html>
