    border-radius: 4px;
}

.message-item strong {
    color: #0d1a47;
}

.message-item small {
    color: #777;
    font-size: 0.8em;
    display: block;
    margin-top: 5px;
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
}

.header h2 {
    margin: 0;
