      WHERE m.id = ? AND (m.recipient_id = ? OR m.sender_id = ?)
    `);
    return stmt.get(messageId, userId, userId);
  }
};

module.exports = {
  db,
  initializeDatabase,
  userDb,
  messageDb
};
