}

.toggle-password:hover {
  opacity: 1;
}

/* Radio Buttons */
.computer-type {
  display: flex;
  gap: 30px;
  margin: 10px 0;
}

.radio-label {
  display: flex;
  align-items: center;
  gap: 10px;
  cursor: pointer;
  font-size: 0.95rem;
}

.radio-label input[type="radio"] {
  display: none;
}

.radio-custom {
  width: 20px;
  height: 20px;
  border: 2px solid #00A8E8;
  border-radius: 50%;
  position: relative;
  transition: all 0.3s;
}

.radio-label input[type="radio"]:checked + .radio-custom {
  background-color: #00A8E8;
  box-shadow: inset 0 0 0 4px white;
}

.radio-text {
  color: #555;
}

/* Buttons */
.btn-primary {
  background: #00A8E8;
  color: white;
  border: none;
  padding: 14px 24px;
  font-size: 1.1rem;
  font-weight: 500;
  border-radius: 4px;
  cursor: pointer;
  transition: background 0.3s, transform 0.1s;
