      padding: 4px;
    }

    .toggle-password:hover {
      color: #333;
    }

    .radio-group {
      margin: 25px 0;
      padding: 20px 0;
      border-top: 1px solid #eee;
      border-bottom: 1px solid #eee;
    }

    .radio-option {
      display: flex;
      align-items: center;
      margin-bottom: 12px;
      cursor: pointer;
      user-select: none;
