            <div class="form-group">
              <label class="form-label">Message:</label>
              <textarea id="bodyInput" class="form-textarea" placeholder="Type your message here..." required></textarea>
            </div>
            <div class="action-buttons">
              <button type="submit" class="btn btn-primary">Send Message</button>
              <button type="reset" class="btn btn-secondary">Clear</button>
            </div>
          </form>
        </div>
      </div>

      <!-- Message Detail View -->
      <div id="messageDetail" class="hidden">
        <div class="content-header">
          <h1 id="detailSubject"></h1>
          <button class="btn btn-secondary" onclick="backToList()"> Back</button>
        </div>
        <div class="content-body">
          <div class="message-detail">
            <div class="message-detail-header">
              <div class="message-detail-from">From: <strong id="detailFrom"></strong></div>
              <div class="message-detail-subject" id="detailFullSubject"></div>
              <div class="message-detail-date" id="detailDate"></div>
            </div>
            <div class="message-detail-body" id="detailBody"></div>
            <div class="action-buttons" style="margin-top: 24px;">
              <button class="btn btn-danger" onclick="deleteMessage()"> Delete Message</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <script>
    let currentUser = null;
    let messages = [];
    let selectedMessage = null;
    let currentTab = 'inbox';

    // Initialize
    window.addEventListener('load', async () => {
      await getCurrentUser();
      await loadInbox();
      await loadUsers();
    });

    async function getCurrentUser() {
      try {
        const response = await fetch('/api/auth/me');
        if (response.ok) {
          const data = await response.json();
          currentUser = data;
          document.getElementById('userEmail').textContent = data.email;
        } else {
          window.location.href = '/';
        }
      } catch (err) {
        console.error('Failed to get current user:', err);
        window.location.href = '/';
      }
    }

    async function loadInbox() {
      try {
        const response = await fetch('/api/messages/inbox');
        if (response.ok) {
          const data = await response.json();
          messages = data.messages;
          renderInbox(data.messages);
          updateUnreadBadge(data.count);
        } else {
          showError('Failed to load inbox', 'inboxError');
        }
      } catch (err) {
        showError('Network error: ' + err.message, 'inboxError');
      }
    }

    async function loadAllMessages() {
      try {
        const response = await fetch('/api/messages/all');
        if (response.ok) {
          const data = await response.json();
          messages = data.messages;
          renderAllMessages(data.messages);
        } else {
          showError('Failed to load messages', 'allError');
        }
      } catch (err) {
        showError('Network error: ' + err.message, 'allError');
      }
    }

    async function loadUsers() {
      try {
        const response = await fetch('/api/messages/users/all');
        if (response.ok) {
          const data = await response.json();
          const select = document.getElementById('recipientSelect');
          data.users.forEach(user => {
            const option = document.createElement('option');
            option.value = user.email;
            option.textContent = user.email;
            select.appendChild(option);
          });
        }
      } catch (err) {
        console.error('Failed to load users:', err);
      }
    }

    function renderInbox(messages) {
      const container = document.getElementById('inboxMessages');

      if (messages.length === 0) {
        container.innerHTML = `
          <div class="empty-state">
            <div class="empty-state-icon"></div>
            <p>No unread messages</p>
          </div>
        `;
        return;
      }

      container.innerHTML = '<div class="messages-list">' + 
        messages.map(msg => `
          <div class="message-item unread" onclick="viewMessage(${msg.id})">
            <div class="message-preview">
              <div class="message-from">From: ${msg.sender_email}</div>
              <div class="message-subject">${escapeHtml(msg.subject)}</div>
              <div class="message-snippet">${escapeHtml(msg.body.substring(0, 80))}...</div>
            </div>
            <div class="message-date">${formatDate(msg.created_at)}</div>
          </div>
        `).join('') +
        '</div>';
    }

    function renderAllMessages(messages) {
      const container = document.getElementById('allMessages');

      if (messages.length === 0) {
        container.innerHTML = `
          <div class="empty-state">
            <div class="empty-state-icon"></div>
            <p>No messages yet</p>
          </div>
        `;
        return;
      }

      container.innerHTML = '<div class="messages-list">' + 
        messages.map(msg => `
          <div class="message-item ${msg.is_read ? '' : 'unread'}" onclick="viewMessage(${msg.id})">
            <div class="message-preview">
              <div class="message-from">From: ${msg.sender_email}</div>
              <div class="message-subject">${escapeHtml(msg.subject)}</div>
              <div class="message-snippet">${escapeHtml(msg.body.substring(0, 80))}...</div>
            </div>
            <div class="message-date">${formatDate(msg.created_at)}</div>
          </div>
        `).join('') +
        '</div>';
    }

    function viewMessage(messageId) {
      const message = messages.find(m => m.id === messageId);
      if (!message) return;

      selectedMessage = message;
      document.getElementById('detailFrom').textContent = message.sender_email;
      document.getElementById('detailFullSubject').textContent = message.subject;
      document.getElementById('detailSubject').textContent = message.subject;
      document.getElementById('detailDate').textContent = formatDate(message.created_at);
      document.getElementById('detailBody').textContent = message.body;

      // Mark as read
      if (!message.is_read) {
        markAsRead(messageId);
        message.is_read = 1;
      }

      // Show message detail view
      document.querySelector(`[id$="Tab"]`).classList.add('hidden');
      document.getElementById('messageDetail').classList.remove('hidden');
    }

    async function markAsRead(messageId) {
      try {
        await fetch(`/api/messages/${messageId}/read`, { method: 'PUT' });
      } catch (err) {
        console.error('Failed to mark as read:', err);
      }
    }

    async function deleteMessage() {
      if (!selectedMessage || !confirm('Delete this message?')) return;

      try {
        const response = await fetch(`/api/messages/${selectedMessage.id}`, {
          method: 'DELETE'
        });

        if (response.ok) {
          showSuccess('Message deleted', 'composeSuccess');
          backToList();
          if (currentTab === 'inbox') {
            await loadInbox();
          } else {
            await loadAllMessages();
          }
        }
      } catch (err) {
        showError('Failed to delete message', 'composeError');
      }
    }

    function backToList() {
      document.getElementById('messageDetail').classList.add('hidden');
      if (currentTab === 'inbox') {
        document.getElementById('inboxTab').classList.remove('hidden');
      } else {
        document.getElementById('allTab').classList.remove('hidden');
      }
    }

    function showTab(tabName) {
      currentTab = tabName;
      document.querySelectorAll('.tab').forEach(t => t.classList.add('hidden'));
      document.getElementById('messageDetail').classList.add('hidden');
      document.querySelectorAll('.sidebar-btn').forEach(b => b.classList.remove('active'));

      if (tabName === 'inbox') {
        document.getElementById('inboxTab').classList.remove('hidden');
        document.querySelectorAll('.sidebar-btn')[0].classList.add('active');
        loadInbox();
      } else if (tabName === 'all') {
        document.getElementById('allTab').classList.remove('hidden');
        document.querySelectorAll('.sidebar-btn')[1].classList.add('active');
        loadAllMessages();
      } else if (tabName === 'compose') {
        document.getElementById('composeTab').classList.remove('hidden');
        document.querySelectorAll('.sidebar-btn')[2].classList.add('active');
      }
    }

    document.getElementById('composeForm').addEventListener('submit', async (e) => {
      e.preventDefault();

      const recipient = document.getElementById('recipientSelect').value;
      const subject = document.getElementById('subjectInput').value.trim();
      const body = document.getElementById('bodyInput').value.trim();

      if (!recipient || !subject || !body) {
        showError('All fields are required', 'composeError');
        return;
      }

      try {
        const response = await fetch('/api/messages/send', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ recipientEmail: recipient, subject, body })
        });

        const data = await response.json();

        if (response.ok) {
          showSuccess('Message sent successfully!', 'composeSuccess');
          document.getElementById('composeForm').reset();
          setTimeout(() => showTab('inbox'), 2000);
        } else {
          showError(data.error || 'Failed to send message', 'composeError');
        }
      } catch (err) {
        showError('Network error: ' + err.message, 'composeError');
      }
    });

    async function logout() {
      try {
        await fetch('/api/auth/logout', { method: 'POST' });
        window.location.href = '/';
      } catch (err) {
        console.error('Logout error:', err);
      }
    }

    function showError(message, elementId) {
      const el = document.getElementById(elementId);
      if (el) {
        el.textContent = message;
        el.style.display = 'block';
      }
    }

    function showSuccess(message, elementId) {
      const el = document.getElementById(elementId);
      if (el) {
        el.textContent = message;
        el.style.display = 'block';
      }
    }

    function updateUnreadBadge(count) {
      const badge = document.getElementById('unreadBadge');
      if (count > 0) {
        badge.textContent = count;
        badge.classList.remove('hidden');
      } else {
        badge.classList.add('hidden');
      }
    }

    function formatDate(dateString) {
      const date = new Date(dateString);
      const today = new Date();
      const isToday = date.toDateString() === today.toDateString();

      if (isToday) {
        return date.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
      } else {
        return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
      }
    }

    function escapeHtml(text) {
      const div = document.createElement('div');
      div.textContent = text;
      return div.innerHTML;
    }
  </script>
</body>
</html>
