<body>
  <div class="container">
    <div class="header">
      <div class="logo"> BlueMind</div>
      <div class="title">v5 - Educational Example</div>
    </div>

    <div class="login-card">
      <div class="error-message" id="errorMessage"></div>
      <div class="success-message" id="successMessage"></div>

      <!-- Login Form -->
      <form id="loginForm">
        <div class="form-title">Identification</div>

        <div class="form-group">
          <label class="form-label">
            <span class="form-label-icon"></span>
            Login
          </label>
          <input 
            type="email" 
            id="loginEmail" 
            class="form-input" 
            placeholder="email@bluemind.net"
            required
          >
        </div>

        <div class="form-group">
          <label class="form-label">
            <span class="form-label-icon"></span>
            Password
          </label>
          <div class="password-wrapper">
            <input 
              type="password" 
              id="loginPassword" 
              class="form-input" 
              placeholder=""
              required
            >
            <button type="button" class="toggle-password" onclick="togglePassword('loginPassword')">

            </button>
          </div>
        </div>

        <div class="radio-group">
          <label class="radio-option">
            <input 
              type="radio" 
              name="computerType" 
              class="radio-input" 
              value="private" 
              checked
            >
            <span class="radio-label"> Private computer</span>
          </label>
          <label class="radio-option">
            <input 
              type="radio" 
              name="computerType" 
              class="radio-input" 
              value="public"
            >
            <span class="radio-label"> Public computer</span>
          </label>
        </div>

        <button type="submit" class="connect-btn">Connect</button>
      </form>

      <div class="toggle-form">
        Don't have an account? <a onclick="switchToRegister()">Register</a>
      </div>

      <!-- Register Form (hidden by default) -->
      <form id="registerForm" class="hidden">
        <div class="form-title">Create Account</div>

        <div class="form-group">
          <label class="form-label">
            <span class="form-label-icon"></span>
            Email
          </label>
          <input 
            type="email" 
            id="registerEmail" 
            class="form-input" 
            placeholder="your@email.com"
            required
          >
        </div>

        <div class="form-group">
          <label class="form-label">
            <span class="form-label-icon"></span>
            Password
          </label>
          <div class="password-wrapper">
            <input 
              type="password" 
              id="registerPassword" 
              class="form-input" 
              placeholder=""
              required
            >
            <button type="button" class="toggle-password" onclick="togglePassword('registerPassword')">

            </button>
          </div>
        </div>

        <div class="form-group">
          <label class="form-label">
            <span class="form-label-icon"></span>
            Confirm Password
          </label>
          <div class="password-wrapper">
            <input 
              type="password" 
              id="registerConfirm" 
              class="form-input" 
              placeholder=""
              required
            >
            <button type="button" class="toggle-password" onclick="togglePassword('registerConfirm')">

            </button>
          </div>
        </div>

        <button type="submit" class="connect-btn">Create Account</button>
      </form>

      <div class="toggle-form" id="toggleRegister">
        Already have an account? <a onclick="switchToLogin()">Login</a>
      </div>
    </div>
  </div>

  <script>
    // Toggle between login and register forms
    function switchToRegister() {
      document.getElementById('loginForm').classList.add('hidden');
      document.getElementById('registerForm').classList.remove('hidden');
      document.querySelector('.toggle-form').classList.add('hidden');
      document.getElementById('toggleRegister').classList.remove('hidden');
      clearMessages();
    }

    function switchToLogin() {
      document.getElementById('loginForm').classList.remove('hidden');
      document.getElementById('registerForm').classList.add('hidden');
      document.querySelector('.toggle-form').classList.remove('hidden');
      document.getElementById('toggleRegister').classList.add('hidden');
      clearMessages();
    }

    // Toggle password visibility
    function togglePassword(fieldId) {
      const field = document.getElementById(fieldId);
      field.type = field.type === 'password' ? 'text' : 'password';
    }

    // Show/hide messages
    function showError(message) {
      const errorEl = document.getElementById('errorMessage');
      errorEl.textContent = message;
      errorEl.style.display = 'block';
      document.getElementById('successMessage').style.display = 'none';
    }

    function showSuccess(message) {
      const successEl = document.getElementById('successMessage');
      successEl.textContent = message;
      successEl.style.display = 'block';
      document.getElementById('errorMessage').style.display = 'none';
    }

    function clearMessages() {
      document.getElementById('errorMessage').style.display = 'none';
      document.getElementById('successMessage').style.display = 'none';
    }

    // Handle login
    document.getElementById('loginForm').addEventListener('submit', async (e) => {
      e.preventDefault();

      const email = document.getElementById('loginEmail').value.trim();
      const password = document.getElementById('loginPassword').value;
      const isPrivateComputer = document.querySelector('input[name="computerType"]:checked').value === 'private';

      if (!email || !password) {
        showError('Email and password are required');
        return;
      }

      const submitBtn = e.target.querySelector('button[type="submit"]');
      submitBtn.classList.add('loading');
      submitBtn.disabled = true;

      try {
        const response = await fetch('/api/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            email: email,
            password: password,
            isPrivateComputer: isPrivateComputer
          })
        });

        const data = await response.json();

        if (response.ok) {
          showSuccess('Login successful! Redirecting...');
          setTimeout(() => {
            window.location.href = '/messages.html';
          }, 1500);
        } else {
          showError(data.error || 'Login failed');
        }
      } catch (err) {
        showError('Network error: ' + err.message);
      } finally {
        submitBtn.classList.remove('loading');
        submitBtn.disabled = false;
      }
    });

    // Handle registration
    document.getElementById('registerForm').addEventListener('submit', async (e) => {
      e.preventDefault();

      const email = document.getElementById('registerEmail').value.trim();
      const password = document.getElementById('registerPassword').value;
      const confirmPassword = document.getElementById('registerConfirm').value;

      if (!email || !password || !confirmPassword) {
        showError('All fields are required');
        return;
      }

      if (password !== confirmPassword) {
        showError('Passwords do not match');
        return;
      }

      if (password.length < 8) {
        showError('Password must be at least 8 characters long');
        return;
      }

      const submitBtn = e.target.querySelector('button[type="submit"]');
      submitBtn.classList.add('loading');
      submitBtn.disabled = true;

      try {
        const response = await fetch('/api/auth/register', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            email: email,
            password: password,
            confirmPassword: confirmPassword
          })
        });

        const data = await response.json();

        if (response.ok) {
          showSuccess('Account created successfully! Redirecting...');
          setTimeout(() => {
            window.location.href = '/messages.html';
          }, 1500);
        } else {
          showError(data.error || 'Registration failed');
        }
      } catch (err) {
        showError('Network error: ' + err.message);
      } finally {
        submitBtn.classList.remove('loading');
        submitBtn.disabled = false;
      }
    });
  </script>
</body>
</html>
