{"content": "<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BlueMind - Register</title>
    <link rel="stylesheet" href="styles.css">
</head>
<body class="login-body">
    <div class="login-container">
        <div class="bluemind-logo">
            <h1>BlueMind</h1>
            <span class="version">v5</span>
        </div>

        <div class="login-box">
            <h2>Create Account</h2>
            <form id="registerForm">
                <div class="form-group">
                    <label for="email">
                        <span class="icon"></span>
                        Email Address
                    </label>
                    <input type="email" id="email" name="email" placeholder="your.email@bluemind.net" required>
                </div>

                <div class="form-group">
                    <label for="password">
                        <span class="icon"></span>
                        Password
                    </label>
                    <div class="password-container">
                        <input type="password" id="password" name="password" placeholder="" required>
                        <button type="button" class="toggle-password" onclick="togglePassword()"></button>
                    </div>
                    <small class="password-hint">Use a strong password with at least 8 characters</small>
                </div>

                <div class="form-group">
                    <label for="confirm-password">
                        <span class="icon"></span>
                        Confirm Password
                    </label>
                    <div class="password-container">
                        <input type="password" id="confirm-password" name="confirm-password" placeholder="" required>
                    </div>
                </div>

                <button type="submit" class="connect-btn">Register</button>

                <div id="errorMessage" class="error-message"></div>
                <div id="successMessage" class="success-message"></div>
            </form>

            <div class="login-footer">
                <p>Already have an account? <a href="/">Login here</a></p>
            </div>
        </div>
    </div>

    <script>
        function togglePassword() {
            const passwordInput = document.getElementById('password');
            if (passwordInput.type === 'password') {
                passwordInput.type = 'text';
            } else {
                passwordInput.type = 'password';
            }
        }

        document.getElementById('registerForm').addEventListener('submit', async (e) => {
            e.preventDefault();
            const errorDiv = document.getElementById('errorMessage');
            const successDiv = document.getElementById('successMessage');
            errorDiv.textContent = '';
            successDiv.textContent = '';

            const email = document.getElementById('email').value;
            const password = document.getElementById('password').value;
            const confirmPassword = document.getElementById('confirm-password').value;

            if (password !== confirmPassword) {
                errorDiv.textContent = 'Passwords do not match';
                return;
            }

            if (password.length < 8) {
                errorDiv.textContent = 'Password must be at least 8 characters long';
                return;
            }

            try {
                const response = await fetch('/api/register', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        email,
                        password
                    })
                });

                const data = await response.json();

                if (data.success) {
                    successDiv.textContent = data.message;
                    setTimeout(() => {
                        window.location.href = '/';
                    }, 2000);
                } else {
                    errorDiv.textContent = data.message || 'Registration failed';
                }
            } catch (error) {
                errorDiv.textContent = 'An error occurred. Please try again.';
                console.error('Registration error:', error);
            }
        });
    </script>
</body>
</html>
