{"content": "<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>BlueMind - Dashboard</title>
    <link rel="stylesheet" href="styles.css">
</head>
<body class="dashboard-body">
    <div class="dashboard-container">
        <header class="dashboard-header">
            <div class="header-left">
                <h1 class="dashboard-title">BlueMind</h1>
            </div>
            <div class="header-right">
                <span id="userEmail" class="user-email"></span>
                <button onclick="logout()" class="logout-btn">Logout</button>
            </div>
        </header>

        <div class="dashboard-content">
            <div class="sidebar">
                <nav class="nav-menu">
                    <button class="nav-item active" onclick="switchTab('inbox')"> Inbox</button>
                    <button class="nav-item" onclick="switchTab('compose')"> Compose</button>
                </nav>
            </div>

            <div class="main-content">
                <!-- Inbox Tab -->
                <div id="inboxTab" class="tab-content active">
                    <h2>Your Messages</h2>
                    <div id="messagesList" class="messages-list">
                        <p class="loading">Loading messages...</p>
                    </div>
                </div>

                <!-- Compose Tab -->
                <div id="composeTab" class="tab-content">
                    <h2>Compose New Message</h2>
                    <form id="composeForm" class="compose-form">
                        <div class="form-group">
                            <label for="recipient">To:</label>
                            <select id="recipient" name="recipient" required>
                                <option value="">Select a recipient...</option>
                            </select>
                        </div>

                        <div class="form-group">
                            <label for="subject">Subject:</label>
                            <input type="text" id="subject" name="subject" placeholder="Message subject">
                        </div>

                        <div class="form-group">
                            <label for="body">Message:</label>
                            <textarea id="body" name="body" rows="10" placeholder="Type your message here..." required></textarea>
                        </div>

                        <button type="submit" class="send-btn">Send Message</button>
                        <div id="composeError" class="error-message"></div>
                        <div id="composeSuccess" class="success-message"></div>
                    </form>
                </div>
            </div>
        </div>

        <!-- Message Modal -->
        <div id="messageModal" class="modal">
            <div class="modal-content">
                <span class="close" onclick="closeModal()">&times;</span>
                <div id="modalBody" class="modal-body">
                    <!-- Message content will be loaded here -->
                </div>
            </div>
        </div>
    </div>

    <script>
        // Initialize dashboard
        async function initializeDashboard() {
            try {
                const response = await fetch('/api/user');
                const data = await response.json();

                if (!data.success) {
                    window.location.href = '/';
                    return;
                }

                document.getElementById('userEmail').textContent = data.email;
                loadMessages();
                loadUsers();
            } catch (error) {
                console.error('Error initializing dashboard:', error);
                window.location.href = '/';
            }
        }

        // Load messages
        async function loadMessages() {
            try {
                const response = await fetch('/api/messages');
                const data = await response.json();

                const messagesList = document.getElementById('messagesList');

                if (!data.success || data.messages.length === 0) {
                    messagesList.innerHTML = '<p class="no-messages">No messages yet</p>';
                    return;
                }

                messagesList.innerHTML = data.messages.map(msg => `
                    <div class="message-item ${msg.is_read ? '' : 'unread'}">
                        <div class="message-header">
                            <span class="sender">${msg.sender_email}</span>
                            <span class="date">${new Date(msg.created_at).toLocaleDateString()}</span>
                        </div>
                        <div class="message-subject">${msg.subject}</div>
                        <button class="read-btn" onclick="viewMessage(${msg.id}, '${msg.sender_email}', '${msg.subject}', '${msg.body.replace(/'/g, "\\'")}', ${msg.is_read})">
                            ${msg.is_read ? 'View' : 'Read'}
                        </button>
                    </div>
                `).join('');
            } catch (error) {
                console.error('Error loading messages:', error);
            }
        }

        // Load users for compose
        async function loadUsers() {
            try {
                const response = await fetch('/api/users');
                const data = await response.json();

                if (data.success) {
                    const recipientSelect = document.getElementById('recipient');
                    data.users.forEach(user => {
                        const option = document.createElement('option');
                        option.value = user.id;
                        option.textContent = user.email;
                        recipientSelect.appendChild(option);
                    });
                }
            } catch (error) {
                console.error('Error loading users:', error);
            }
        }

        // Switch tabs
        function switchTab(tab) {
            // Hide all tabs
            document.querySelectorAll('.tab-content').forEach(t => t.classList.remove('active'));
            document.querySelectorAll('.nav-item').forEach(n => n.classList.remove('active'));

            // Show selected tab
            document.getElementById(tab + 'Tab').classList.add('active');
            event.target.classList.add('active');
        }

        // View message
        function viewMessage(id, sender, subject, body, isRead) {
            const modal = document.getElementById('messageModal');
            const modalBody = document.getElementById('modalBody');

            modalBody.innerHTML = `
                <h2>${subject}</h2>
                <div class="message-meta">
                    <p><strong>From:</strong> ${sender}</p>
                    <p><strong>Time:</strong> ${new Date().toLocaleString()}</p>
                </div>
                <div class="message-content">
                    ${body}
                </div>
            `;

            modal.style.display = 'block';

            // Mark as read
            if (!isRead) {
                markAsRead(id);
            }
        }

        // Mark message as read
        async function markAsRead(id) {
            try {
                await fetch(`/api/messages/${id}/read`, {
                    method: 'POST'
                });
                loadMessages();
            } catch (error) {
                console.error('Error marking message as read:', error);
            }
        }

        // Close modal
        function closeModal() {
            document.getElementById('messageModal').style.display = 'none';
        }

        // Close modal when clicking outside
        window.onclick = function(event) {
            const modal = document.getElementById('messageModal');
            if (event.target == modal) {
                modal.style.display = 'none';
            }
        }

        // Compose message
        document.getElementById('composeForm').addEventListener('submit', async (e) => {
            e.preventDefault();
            const errorDiv = document.getElementById('composeError');
            const successDiv = document.getElementById('composeSuccess');
            errorDiv.textContent = '';
            successDiv.textContent = '';

            const recipientId = document.getElementById('recipient').value;
            const subject = document.getElementById('subject').value;
            const body = document.getElementById('body').value;

            try {
                const response = await fetch('/api/messages', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        receiver_id: recipientId,
                        subject,
                        body
                    })
                });

                const data = await response.json();

                if (data.success) {
                    successDiv.textContent = 'Message sent successfully!';
                    document.getElementById('composeForm').reset();
                    setTimeout(() => {
                        successDiv.textContent = '';
                        switchTab('inbox');
                        loadMessages();
                    }, 2000);
                } else {
                    errorDiv.textContent = data.message || 'Failed to send message';
                }
            } catch (error) {
                errorDiv.textContent = 'An error occurred. Please try again.';
                console.error('Error sending message:', error);
            }
        });

        // Logout
        async function logout() {
            try {
                await fetch('/api/logout', {
                    method: 'POST'
                });
                window.location.href = '/';
            } catch (error) {
                console.error('Error logging out:', error);
            }
        }

        // Initialize on page load
        initializeDashboard();
    </script>
</body>
</html>
