const db = require('../models/database');
const { requireLogin } = require('../middleware/auth');

const router = express.Router();

// Get messages for logged-in user
router.get('/', requireLogin, (req, res) => {
  db.getMessagesByRecipient(req.session.userId, (err, messages) => {
    if (err) {
      console.error('Database error:', err);
      return res.status(500).json({ error: 'Failed to retrieve messages' });
    }

    res.json(messages || []);
  });
});

// Send a new message
router.post('/', requireLogin, (req, res) => {
  try {
    const { recipientId, subject, body } = req.body;

    // Input validation
    if (!recipientId || !subject || !body) {
      return res.status(400).json({ error: 'Recipient, subject, and body are required' });
    }

    // Prevent user from messaging themselves
    if (parseInt(recipientId) === req.session.userId) {
      return res.status(400).json({ error: 'Cannot send messages to yourself' });
    }

    // Create message
    db.createMessage(req.session.userId, recipientId, subject, body, (err, messageId) => {
      if (err) {
        console.error('Database error:', err);
        return res.status(500).json({ error: 'Failed to send message' });
      }

      res.json({ message: 'Message sent successfully', messageId });
    });
  } catch (err) {
    console.error('Error sending message:', err);
    res.status(500).json({ error: 'Failed to send message' });
  }
});

// Mark message as read
router.put('/:id/read', requireLogin, (req, res) => {
  const messageId = req.params.id;

  // Note: In production, verify that the user has permission to mark this message as read
  db.markMessageAsRead(messageId, (err) => {
    if (err) {
      console.error('Database error:', err);
      return res.status(500).json({ error: 'Failed to mark message as read' });
    }

    res.json({ message: 'Message marked as read' });
  });
});

module.exports = router;
"}

{"content": "/* ========================================
   GLOBAL STYLES
   ======================================== */

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
    color: #333;
    line-height: 1.6;
}

/* ========================================
   LOGIN PAGE STYLES
   ======================================== */

.login-page {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px;
}

.login-container {
    width: 100%;
    max-width: 450px;
}

.login-box {
    background: white;
    border-radius: 8px;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
    overflow: hidden;
}

.brand {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 30px 20px;
    text-align: center;
}

.brand h1 {
    font-size: 28px;
    margin-bottom: 5px;
}

.brand .version {
    font-size: 12px;
    opacity: 0.8;
}

.form-section {
    padding: 30px;
}

.form-section h2 {
    font-size: 20px;
    margin-bottom: 20px;
    color: #333;
}

.form-group {
    display: flex;
    flex-direction: column;
    gap: 15px;
}

.form-group.hidden {
    display: none;
}

.form-field {
    display: flex;
    flex-direction: column;
}

.form-field label {
    font-weight: 600;
    margin-bottom: 8px;
    font-size: 14px;
    display: flex;
    align-items: center;
    gap: 8px;
}

.form-field label .icon {
    font-size: 16px;
}

.form-field input[type="email"],
.form-field input[type="password"],
.form-field input[type="text"],
.form-field textarea {
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 14px;
    font-family: inherit;
}

.form-field input[type="email"]:focus,
.form-field input[type="password"]:focus,
.form-field input[type="text"]:focus,
.form-field textarea:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
}

.form-field small {
    font-size: 12px;
    color: #666;
    margin-top: 4px;
}

.password-wrapper {
    position: relative;
    display: flex;
}

.password-wrapper input {
    flex: 1;
}

.toggle-password {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    cursor: pointer;
    font-size: 16px;
    padding: 4px 8px;
}

.form-options {
    display: flex;
