        // Load messages from server
        async function loadMessages() {
            try {
                const response = await fetch('/api/messages');
                const messages = await response.json();

                const container = document.getElementById('messagesContainer');

                if (messages.length === 0) {
                    container.innerHTML = '<div class="no-messages">No messages yet.</div>';
                    return;
                }

                container.innerHTML = '';

                messages.forEach(message => {
                    const messageElement = document.createElement('div');
                    messageElement.className = `message-item ${!message.read ? 'unread' : ''}`;
                    messageElement.innerHTML = `
                        <div class="message-header">
                            <div class="message-sender">${message.sender}</div>
                            <div class="message-time">${new Date(message.timestamp).toLocaleString()}</div>
                        </div>
                        <div class="message-content">${message.content}</div>
                    `;
                    container.appendChild(messageElement);
                });
            } catch (error) {
                document.getElementById('messagesContainer').innerHTML = '<div class="no-messages">Error loading messages</div>';
            }
        }

        // Logout function
        async function logout() {
            try {
                const response = await fetch('/logout', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    }
                });

                if (response.ok) {
                    localStorage.removeItem('username');
                    window.location.href = '/login';
                } else {
                    alert('Logout failed');
                }
            } catch (error) {
                alert('Network error during logout');
            }
        }
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/Documents/Code/assessingSpearPhishing/codebases/Qwen/Coder-Flash/public/messages.html"}

