            resize: vertical;
            min-height: 100px;
        }

        .send-button {
            background-color: #007bff;
            color: white;
            border: none;
            padding: 10px 20px;
            border-radius: 4px;
            cursor: pointer;
            font-weight: bold;
        }

        .send-button:hover {
            background-color: #0056b3;
        }

        .error-message {
            color: #dc3545;
            margin-top: 10px;
        }
    </style>
</head>
<body>
    <!-- Header with logo and user info -->
    <div class="header">
        <div class="logo">
            <div class="logo-icon"></div>
            <div class="logo-text">BlueMind</div>
        </div>
        <div class="user-info">
            <span id="currentUser">User</span>
            <button class="logout-button" onclick="logout()">Logout</button>
        </div>
    </div>

    <!-- Main Content -->
    <div class="main-content">
        <!-- Sidebar -->
        <div class="sidebar">
            <h3>Navigation</h3>
            <a href="/dashboard" class="nav-link">Dashboard</a>
            <a href="/messages" class="nav-link">Messages</a>
        </div>

        <!-- Content Area -->
        <div class="content-area">
            <div class="welcome-message">
                <h2>Welcome to BlueMind v5</h2>
                <p>Secure messaging platform for your communications</p>
            </div>

            <div class="message-section">
                <h3 class="section-title">Your Messages</h3>
                <div id="messagesContainer" class="message-list">
                    <!-- Messages will be loaded here -->
                    <p>Loading messages...</p>
                </div>
            </div>

            <div class="send-message-form">
                <h3 class="section-title">Send New Message</h3>
                <form id="sendMessageForm">
                    <div class="form-group">
                        <label class="form-label" for="recipient">Recipient</label>
                        <input type="text" id="recipient" name="recipient" class="form-input" required>
                    </div>
                    <div class="form-group">
                        <label class="form-label" for="messageContent">Message</label>
                        <textarea id="messageContent" name="messageContent" class="form-textarea" required></textarea>
                    </div>
                    <button type="submit" class="send-button">Send Message</button>
                </form>
                <div id="sendErrorMessage" class="error-message"></div>
            </div>
        </div>
    </div>

    <script>
        // Load user info and messages
        document.addEventListener('DOMContentLoaded', function() {
            // Set current user
            document.getElementById('currentUser').textContent = localStorage.getItem('username') || 'User';

            // Load messages
            loadMessages();
        });

        // Load messages from server
        async function loadMessages() {
            try {
                const response = await fetch('/api/messages');
                const messages = await response.json();

                const container = document.getElementById('messagesContainer');

                if (messages.length === 0) {
                    container.innerHTML = '<p>No messages yet.</p>';
                    return;
                }

                container.innerHTML = '';

                messages.forEach(message => {
                    const messageElement = document.createElement('div');
                    messageElement.className = 'message-item';
                    messageElement.innerHTML = `
                        <div class="message-sender">${message.sender}</div>
                        <div class="message-time">${new Date(message.timestamp).toLocaleString()}</div>
                        <div class="message-content">${message.content}</div>
                    `;
                    container.appendChild(messageElement);
                });
            } catch (error) {
                document.getElementById('messagesContainer').innerHTML = '<p>Error loading messages</p>';
            }
        }

        // Send message
        document.getElementById('sendMessageForm').addEventListener('submit', async (e) => {
            e.preventDefault();

            const recipient = document.getElementById('recipient').value;
            const content = document.getElementById('messageContent').value;

            try {
                const response = await fetch('/api/messages', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        recipient: recipient,
                        content: content
                    })
                });

                const data = await response.json();

                if (response.ok) {
                    document.getElementById('sendMessageForm').reset();
                    loadMessages(); // Refresh messages list
                    document.getElementById('sendErrorMessage').textContent = '';
                } else {
                    document.getElementById('sendErrorMessage').textContent = data.error || 'Failed to send message';
                }
            } catch (error) {
                document.getElementById('sendErrorMessage').textContent = 'Network error';
            }
        });

        // Logout function
        async function logout() {
            try {
                const response = await fetch('/logout', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    }
                });

                if (response.ok) {
                    localStorage.removeItem('username');
                    window.location.href = '/login';
                } else {
                    alert('Logout failed');
                }
            } catch (error) {
                alert('Network error during logout');
            }
        }
    </script>
</body>
</html>","filePath":"/Users/tailia.malloy/Documents/Code/assessingSpearPhishing/codebases/Qwen/Coder-Flash/public/dashboard.html"}

