        }

        .logo {
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .logo-icon {
            width: 30px;
            height: 30px;
            background-color: #007bff;
            clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
        }

        .logo-text {
            color: white;
            font-size: 24px;
            font-weight: bold;
        }

        /* Registration panel styling */
        .register-container {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-grow: 1;
        }

        .register-panel {
            background-color: white;
            width: 400px;
            padding: 30px;
            border-radius: 8px;
