      color: #333;
      margin-bottom: 10px;
    }

    .actions {
      display: flex;
      gap: 15px;
      margin-top: 15px;
    }

    .btn {
      padding: 10px 20px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
      text-decoration: none;
      display: inline-block;
      text-align: center;
