            <li class="message-item">
              <div class="message-header">
                <span class="message-sender">From: <%= message.sender %></span>
                <span class="message-date"><%= new Date(message.timestamp).toLocaleString() %></span>
              </div>
              <div class="message-subject"><%= message.subject %></div>
              <div class="message-preview"><%= message.content.substring(0, 100) %><% if (message.content.length > 100) { %>...<% } %></div>
              <div class="message-actions">
                <a href="/message/<%= message._id %>" class="view-btn">View Message</a>
              </div>
            </li>
          <% }); %>
        </ul>
      <% } %>
    </div>
  </div>
</body>
</html>"}

