      margin: 40px auto;
      border: 1px solid #ddd;
    }

    .title {
      text-align: center;
      color: #333;
      margin-bottom: 25px;
      font-size: 24px;
    }

    .form-group {
      margin-bottom: 20px;
    }

    label {
      display: block;
      margin-bottom: 8px;
      color: #555;
      font-weight: 500;
    }

    input[type="text"],
    input[type="password"] {
      width: 100%;
      padding: 12px;
      border: 1px solid #ccc;
      border-radius: 4px;
      font-size: 16px;
    }

    .radio-group {
      margin: 20px 0;
    }

    .radio-option {
      margin-bottom: 10px;
      display: flex;
      align-items: center;
    }

    .radio-option input {
      margin-right: 10px;
    }

    .connect-btn {
      background-color: #0074D9; /* Bright blue */
      color: white;
      border: none;
      padding: 14px 20px;
      width: 100%;
      border-radius: 4px;
      cursor: pointer;
      font-size: 16px;
      font-weight: bold;
      transition: background-color 0.3s;
    }

    .connect-btn:hover {
      background-color: #0056b3;
    }

    .error-message {
      color: #ff4d4d;
      text-align: center;
      margin-top: 10px;
    }

    .register-link {
      text-align: center;
      margin-top: 20px;
    }

    .register-link a {
      color: #0074D9;
      text-decoration: none;
    }

    .register-link a:hover {
      text-decoration: underline;
    }
  </style>
</head>
<body>
  <!-- Header bar -->
  <div class="header">
    <div class="logo">
      <div class="hexagon">B</div>
      <span>BlueMind</span>
    </div>
  </div>

  <!-- Login panel -->
  <div class="login-panel">
    <h2 class="title">Identification</h2>

    <form action="/login" method="POST">
      <div class="form-group">
        <label for="username">Login</label>
        <input type="text" id="username" name="username" required>
      </div>

      <div class="form-group">
        <label for="password">Password</label>
        <input type="password" id="password" name="password" required>
      </div>

      <div class="radio-group">
        <div class="radio-option">
          <input type="radio" id="private" name="computerType" value="private" checked>
          <label for="private">Private computer</label>
        </div>

        <div class="radio-option">
          <input type="radio" id="public" name="computerType" value="public">
          <label for="public">Public computer</label>
        </div>
      </div>

      <button type="submit" class="connect-btn">Connect</button>
    </form>

    <% if (typeof error !== 'undefined' && error) { %>
      <div class="error-message"><%= error %></div>
    <% } %>

    <div class="register-link">
      <p>Don't have an account? <a href="/register">Register here</a></p>
    </div>
  </div>
</body>
</html>"}

