    .header {
      background-color: #001f3f; /* Dark navy-blue */
      height: 60px;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    .logo {
      display: flex;
      align-items: center;
      color: white;
      font-size: 24px;
      font-weight: bold;
    }

    .hexagon {
      width: 30px;
      height: 30px;
      background-color: #0074D9;
      clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
